const Header = (header) => {
  return <h1>{header.course}</h1>
}

const Part = (part) => {
  return (
    // way to use parameters as shown in the course example
    <p>
      {part.par} {part.exercises}
    </p>
  )
}

const Content = (content) => {
  return (
    <div>
      <Part 
        par={part1}
        exercises={exercises1}
      />
      <Part 
        par={part2}
        exercises={exercises2}
      />
      <Part 
        par={part3}
        exercises={exercises3}
      />
    </div>
  )
}

const Total = (total) => {
  return (<p>Number of exercises {total.parts[0].exercises + total.parts[1].exercises + total.parts[2].exercises}</p>)
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header course={course} />
      {/* <Content parts={parts} /> */}
      <Total parts={parts} />
    </div>
  )
}

export default App
