import { useState } from 'react'

const Button = ({click, text}) => (
  <button onClick={click}>{text}</button>
)

const Average = ({good, neutral, bad}) => {
  const unit = 1 / (good + neutral + bad)
  return (unit * good - unit * bad)
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <h1>give feedback</h1>
      <Button click={() => setGood(good + 1)} text = 'good' />
      <Button click={() => setNeutral(neutral + 1)} text = 'neutral' />
      <Button click={() => setBad(bad + 1)} text = 'bad' />
      <h1>statistics</h1>
      good {good} <br /> neutral {neutral} <br /> bad {bad} <br />
      all {good + bad + neutral} <br />
      average 
    </div>
  )
}

export default App
