const Header = ({header}) => {
  return <h1>{header}</h1>
}

const Part = (part) => {
  return (
    // way to use parameters as shown in the course example
    <p>
      {part.par} {part.exercises}
    </p>
  )
}

const Content = ({content}) => {
  return (
    <div>
      <Part 
        par={content[0].name}
        exercises={content[0].exercises}
      />
      <Part 
        par={content[1].name}
        exercises={content[1].exercises}
      />
      <Part 
        par={content[2].name}
        exercises={content[2].exercises}
      />
    </div>
  )
}

const Course = ({course}) => {
  return (
    <div>
      <Header header={course.name} />
      <Content content={course.parts} />
    </div>
  )
}

const App = () => {
  const courses = [
    {
      id: 1,
      name: 'Half Stack application development',
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        }
      ]
    }
  ]

  return (
    <div>
        {courses.map(course => 
          <Course key={course.id} course={course} />
        )}
    </div>
  )
}

export default App
