import { useState } from 'react'

const Names = ({name, number}) => (
  <div>{name} {number}</div>
)

const Filter = ({persons, find}) => {
  const filteredName = persons.filter(person => person.name.toLowerCase().startsWith(find.toLowerCase()))
  return (filteredName)
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-1234567'}
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [find, setFind] = useState('')

  const addName = (event) => {
    event.preventDefault()
    if (persons.some(person => JSON.stringify(person.name) === JSON.stringify(newName)))
      alert(newName +' is already added to the phonebook')
    else
    {
      setPersons([...persons, { name: newName, number: newNumber }]);
      setNewName('');
      setNewNumber('')
    }
  }

  const handleNames = (event) => {setNewName(event.target.value)}
  const handleNumbers = (event) => {setNewNumber(event.target.value)}
  const handleFilter = (event) => {setFind(event.target.value)}

  const filteredName = persons.filter(person => person.name.toLowerCase().startsWith(find.toLowerCase()))

  return (
    <div>
      <h2>Phonebook</h2>
      {'filter shown with '}
      <input
        value={find}
        onChange={handleFilter}
        />
      <h2>add a new</h2>
      <form onSubmit={addName}>
        <div> {"name: "}
          <input
            value={newName}
            onChange={handleNames}
            /> <br /> {"number: "}
          <input
            value={newNumber}
            onChange={handleNumbers}
            /> <br />
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {filteredName.map(person => 
          <Names key={person.name} name={person.name} number={person.number} />
        )}
    </div>
  )
}

export default App
